import { useState } from 'react'
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group"

export default function CreateGame() {
  const [categoryType, setCategoryType] = useState('random')
  const [customCategories, setCustomCategories] = useState([''])

  const handleCategoryChange = (index: number, value: string) => {
    const newCategories = [...customCategories]
    newCategories[index] = value
    setCustomCategories(newCategories)
  }

  const addCategory = () => {
    setCustomCategories([...customCategories, ''])
  }

  const removeCategory = (index: number) => {
    const newCategories = customCategories.filter((_, i) => i !== index)
    setCustomCategories(newCategories)
  }

  const startGame = () => {
    // Hier würden Sie die Logik zum Starten des Spiels implementieren
    console.log('Spiel starten mit:', { categoryType, customCategories })
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-purple-900 via-blue-900 to-cyan-900 text-white p-4">
      <h1 className="text-4xl font-bold mb-8">Neues Spiel erstellen</h1>
      <div className="w-full max-w-md space-y-6">
        <RadioGroup value={categoryType} onValueChange={setCategoryType} className="space-y-2">
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="random" id="random" />
            <Label htmlFor="random">Zufällige Kategorien</Label>
          </div>
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="custom" id="custom" />
            <Label htmlFor="custom">Eigene Kategorien</Label>
          </div>
        </RadioGroup>

        {categoryType === 'custom' && (
          <div className="space-y-4">
            {customCategories.map((category, index) => (
              <div key={index} className="flex space-x-2">
                <Input
                  type="text"
                  value={category}
                  onChange={(e) => handleCategoryChange(index, e.target.value)}
                  placeholder={`Kategorie ${index + 1}`}
                  className="flex-grow bg-gray-700 text-white"
                />
                <Button onClick={() => removeCategory(index)} variant="destructive">
                  Entfernen
                </Button>
              </div>
            ))}
            <Button onClick={addCategory} variant="outline">Kategorie hinzufügen</Button>
          </div>
        )}

        <Button 
          onClick={startGame}
          className="w-full bg-gradient-to-r from-purple-500 to-blue-500 hover:from-purple-600 hover:to-blue-600"
        >
          Spiel starten
        </Button>
      </div>
    </div>
  )
}